import { useState } from "react";
import { Container, Row, Col, Image, Card, Button } from "react-bootstrap";
import colorSharp2 from "../assets/img/color-sharp2.png";
import fridayfinder from "../assets/img/friday-finder.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
  // Sample project data
  const projects = [
    {
      title: "Project 1",
      description: "Description of Project 1.",
      image: colorSharp2, // Replace this with the actual image for Project 1
      // Add any other properties you need for Project 1
    },
    {
      title: "Project 2",
      description: "Description of Project 2.",
      image: fridayfinder, // Replace this with the actual image for Project 2
      // Add any other properties you need for Project 2
    },
    // Add more projects as needed
  ];

  // State to manage the visibility and animation of the project section
  const [isVisible, setIsVisible] = useState(false);

  // Function to handle the visibility change when the section comes into view
  const handleVisibilityChange = (visible) => {
    setIsVisible(visible);
  };

  return (
    <section className="project" id="project">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility onChange={handleVisibilityChange}>
              {({ isVisible: isSectionVisible }) => (
                <div
                  className={`animate__animated ${
                    isSectionVisible ? "animate__fadeIn" : ""
                  }`}
                >
                  <h2>Projects</h2>
                  <Row>
                    {projects.map((project, index) => (
                      <Col key={index} md="12" className="mb-4">
                        <Card>
                          <Row>
                            <Col md="4">
                              <Image src={project.image} alt={project.title} fluid style={{ height: "200px", width: "200px" }} />
                            </Col>
                            <Col md="8">
                              <Card.Body>
                                <Card.Title>{project.title}</Card.Title>
                                <Card.Text>{project.description}</Card.Text>
                                {/* Add any other card content or buttons as needed */}
                                <Button variant="primary">Learn More</Button>
                              </Card.Body>
                            </Col>
                          </Row>
                        </Card>
                      </Col>
                    ))}
                  </Row>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
};
